document.addEventListener("DOMContentLoaded",function(){const e=document.querySelector(".popup-overlay"),o=document.querySelector(".popup-close");setTimeout(()=>{e.classList.add("active"),sessionStorage.setItem("melissaPopupShown","true")},1e3);function t(){e.classList.remove("active")}o.addEventListener("click",t),e.addEventListener("click",function(){t()}),document.addEventListener("keydown",function(n){n.key==="Escape"&&e.classList.contains("active")&&t()})});
